# Only SYS_ADMIN capabilite
isPrivilegedContainer(container, i, beggining_of_path) = path {
	not container.securityContext.privileged == true
	path = [sprintf("%vcontainers[%v].securityContext.capabilities.add[%v]", [beggining_of_path, format_int(i, 10), format_int(k, 10)]) | capabilite = container.securityContext.capabilities.add[k]; capabilite == "SYS_ADMIN"]
	count(path) > 0
}

# Only securityContext.privileged == true
isPrivilegedContainer(container, i, beggining_of_path) = path {
	container.securityContext.privileged == true
	path1 = [sprintf("%vcontainers[%v].securityContext.capabilities.add[%v]", [beggining_of_path, format_int(i, 10), format_int(k, 10)]) | capabilite = container.securityContext.capabilities.add[k]; capabilite == "SYS_ADMIN"]
	count(path1) < 1
	path = [sprintf("%vcontainers[%v].securityContext.privileged", [beggining_of_path, format_int(i, 10)])]
